const initialItems = [
    { id: 1, description: "Passports", quantity: 2, packed: false },
    { id: 2, description: "Socks", quantity: 12, packed: true },
    { id: 3, description: "Charger", quantity: 1, packed: false },
  ];
  
  function App() {
    return (
      <div className="app">
        <Logo />
        <Form />
        <PackingList />
        <Stats />
      </div>
    );
  }
  
  function Logo() {
    return <h1> ü§ó Far Away üëΩ </h1>;
  }
  
  function Form() {

    //Form darf nicht submiten

    return (
      <div className="add-form">
        <h3>What do you need for your Trip? üí´</h3>
        {/* Hier kommt select, option num iterate und ein Add Button */}
      </div>
    );
  }

  function PackingList() {
    return (
      <div className="list">
        <ul>
          {initialItems.map(item => <Item item={item}/>)}
        </ul>
      </div>
    );
  }
  
  function Item({item}) {
    // const strickedThroughStyle = item.packed ? {textDecoration: 'line-through'} : {};

    return (
      <li>
        <span>
            {item.quantity} {item.description}
        </span>
        <button>‚ùå</button>
      </li>
    );
  }
  
  function Stats() {
    return (
      <footer className="stats">
        <em>You have X items on your list & you have already packed X (X%)</em>
      </footer>
    );
  }
  
  export default App;
